const redux=require('redux')
const createStore=redux.createStore
const combineReducers=redux.combineReducers
const ADDITION='ADDITION'
const SUBTRACTION ='SUBTRACTION'
function Addition(){
    return{
        type:ADDITION,
        info:'addition of two numbers'
    }

}
function Subtraction(){
    return{
        type:SUBTRACTION,
        info:'subtraction of two numbers'

    }
}
const  initialState={
    value1:30
    
    
}
const initialState1={
    value2:12
}

const Additionreducer=(state=initialState,action)=>{
    switch(action.type){
        case ADDITION:return{
            ...state,
            value1:state.value1+1
        }
        
        default: return state
    }
}
const SubtractionReducer=(state=initialState1,action)=>{
    switch(action.type){
        case SUBTRACTION:return{
            ...state,
            value2:state.value2+1
        }
        default: return state

    }
}
const rootReducer=combineReducers({
    addition:Additionreducer,
    Subtraction:SubtractionReducer
})
const store=redux.createStore(rootReducer)
const unsubscribe=store.subscribe(()=>console.log('updated state',store.getState()))
store.dispatch(Addition())
store.dispatch(Addition())
store.dispatch(Addition())
unsubscribe()